CREATE TABLE IF NOT EXISTS orders (id TEXT PRIMARY KEY, customer_name TEXT NOT NULL, customer_email TEXT NOT NULL, customer_mobile TEXT NOT NULL, shipping_address TEXT NOT NULL, total_amount REAL NOT NULL, shipping_cost REAL NOT NULL, status TEXT NOT NULL DEFAULT 'New Order', transaction_id TEXT UNIQUE, cashfree_order_id TEXT, cashfree_payment_id TEXT, logistics_id TEXT, created_at TEXT DEFAULT (datetime('now')), updated_at TEXT DEFAULT (datetime('now')));
